import './App.css';

import { Component } from 'react';

class App extends Component {

  constructor(props)
  {
    super(props);
    this.state = {
      notes: []
    }
  }

  API_URL = "http://localhost:5009/";

  //Runs when page refreshes
  componentDidMount()
  {
    this.refreshNotes();
  }

  //Method below will get notes from the API for this component
  async refreshNotes() {

    fetch(this.API_URL+"api/ToDoApp/GetNotes")
      .then(response=>response.json())
      .then(data =>{
        this.setState({notes: data});
      })
  }

  //Method will delete a note entry
  async deleteNote(id) {
    fetch(this.API_URL+`api/ToDoApp/DeleteNotes?id=${id}`,
      {
        method: 'Delete', //HTTP method for deletion
        headers: {
          'Content-Type': 'application/json', 
        }
      }
    );
  }

  render()
  {
    const {notes} = this.state;
    return (
      <div className="App">
        <h2>Todo App</h2>
        {notes.map(
          note =>
            <>
              <p>
                {note.id}
                <b>* {note.description}</b>
              </p>
              {/* <button onClick={(note.id) => deleteNote(note.)}>Delete</button> */}
              <button onClick={() => handleDelete(1)}>Delete Item with ID 1</button>
            </>
        )}
      </div>
    );
  }
}

export default App;
