import './App.css';

import { Component } from 'react';

class App extends Component {

  constructor(props)
  {
    super(props);
    this.state = {
      notes: []
    }
  }

  API_URL = "http://localhost:5009/";

  //Runs when page refreshes
  componentDidMount()
  {
    this.refreshNotes();
  }

  //Method below will get notes from the API for this component
  async refreshNotes() {

    fetch(this.API_URL+"api/ToDoApp/GetNotes")
      .then(response=>response.json())
      .then(data =>{
        this.setState({notes: data});
      })
  }

  //Method
  async addNote()
  {
    var newNotes = document.getElementById("newNotes").value;

    const data = new FormData();

    data.append("newNotes", newNotes);

    fetch(this.API_URL+`api/ToDoApp/AddNotes`,
      {
        method: 'POST',  
        body: data
      }
    ).then(response => response.json())
    .then((result) => {
      alert(result);
      this.refreshNotes();
    })
  } // End of addNote function

  async editNote(id)
  {
    console.log("id = " + id);

    var editNotes = document.getElementById(`editNotes_${id}`).value;

    const data = new FormData();

    data.append("editNotes", editNotes);


    fetch(this.API_URL+`api/ToDoApp/EditNotes?id=${id}`,
      {
        method: 'POST',  
        body: data
      }
    ).then(response => response.json())
    .then((result) => {
      alert(result);
      this.refreshNotes();
    })
  } // End of editNote function

  //Method will delete a note entry
  async deleteNote(id) {
    fetch(this.API_URL+`api/ToDoApp/DeleteNotes?id=${id}`,
      {
        method: 'Delete', //HTTP method for deletion
        headers: {
          'Content-Type': 'application/json', 
        }
      }
    )
    .then(()=>this.refreshNotes());
  }



  render()
  {
    const {notes} = this.state;
    return (
      <div className="App">
        <h2>Todo App</h2>
        {notes.map(
          note =>
            <>
              <p>
                {note.id}
                <b>* {note.description}</b>
              </p>
              <input type="text" id={`editNotes_${note.id}`} name="editNotes" />
              <button onClick={()=>this.editNote(note.id)}>Edit Notes</button>
              <button onClick={()=>this.deleteNote(note.id)}>Delete</button>
            </>
        )}

        <input type="text" id="newNotes" name="newNotes" />
        <button onClick={()=>this.addNote()}>Add Notes</button>
      </div>
    );
  }
}

export default App;
